import React,{useState} from "react";
import "./Form.css"
function Form(){
    let [username,setUsername] = useState("");
    let [cardnumber,setCardNumber] = useState("");
    let [month,setMonth] = useState("");
    let [year,setYear] = useState("");
    let [cvv,setCvv] = useState("");

    function handleSubmit(e){
          e.preventDefault();
    }

    return(
        <div className="main">
            <form onSubmit={handleSubmit}>
                <div className="gapp">
                    <p>CARDHOLDER NAME</p>
                    <input className="inputbox cardname" type="text" placeholder="e.g. Jane Appleseed" onChange={(e)=>{e.preventDefault();setUsername(e.target.value)}}></input>
                </div>
                <div className="gapp">
                    <p>CARD NUMBER</p>
                    <input className="inputbox cardnumber" type="number" placeholder="e.g. 1234 5678 9123 0000" onChange={(e)=>{e.preventDefault();setCardNumber(e.target.value)}}></input>
                </div>
                <div className="date gapp">
                    <div className="datediv">
                        <p>EXP.DATE (MM/YY)</p>
                        <div className="dateMMYY">
                            <input className="inputbox dateinput" placeholder="MM" type="number" onChange={(e)=>{e.preventDefault();setMonth(e.target.value)}}></input>
                            <input className="inputbox dateinput" placeholder="YY" type="number" onChange={(e)=>{e.preventDefault();setYear(e.target.value)}}></input>
                        </div>
                    </div>
                    <div className="cvvdiv">
                        <p>CVC</p>
                        <input className="inputbox cvvinput" placeholder="e.g. 123" type="number" onChange={(e)=>{e.preventDefault();setCvv(e.target.value)}}></input>
                    </div>
                </div>
                <div className="gapp">
                    <button className="btn">Confirm</button>
                </div>
            </form>
        </div>
    );
}

export default Form;
